% Vector de fuerzas distribuidas del elemento finito unidimensionalfunction [FEL] = FELEMD(XYE,CAE,WELE)  % Entrada:  XYE():  coordenadas de los nudos del elemento  %           CAE():  propiedades del catergoría del elemento  %           WELE:   carga distribuida  %  % Salida:   FEL():  vector de fuerzas distribuida del elemento    TIPE = CAE(3);  % código del tipo de elemento  LELE = abs(XYE(2,1)-XYE(1,1)); % longitud del elemento medida entre nudos I y J    switch TIPE    case 101 % elemento de fuerza axial unimensional lineal de cont c0      [FEL] = FAXIAL(LELE,WELE);    case 102 % elemento de fuerza axial unidimensional cuadrático de cont c0      [FEL] = FAXIAC(LELE,WELE);    case 111 % elemento de flexión unidimensional cúbico de cont c1      [FEL] = FBEAME(LELE,WELE);    case 112 %%M-DV%% elemento de flexión unidimensional lineal de continuidad c0 (Timoshenko con Ks 1 puntos de Gauss)      [FEL] = FBEAMT1(LELE,WELE);    case 113 %%M-DV%% elemento de flexión unidimensional lineal de continuidad c0 (Timoshenko con Ks 2 puntos de Gauss)      [FEL] = FBEAMT2(LELE,WELE);    otherwise      % pendiente  end end% ------------------------------------------------------------------------% Vector de fuerzas equivalentes a una carga distribuida uniforme en un % elemento de fuerza axial unidimensional lineal de continuidad c0function [FEL] = FAXIAL(LELE,WELE)  % Entrada:  LELE:   longitud del elemento  %           WELE:   carga distribuida  % Salida:   FEL():  vector de fuerzas distribuida del elemento    FEL = (WELE*LELE/2)*[1 ; 1];end% ------------------------------------------------------------------------% Vector de fuerzas equivalentes a una carga distribuida uniforme en un % elemento de fuerza axial unidimensional cuadrático de continuidad c0function [FEL] = FAXIAC(LELE,WELE)  % Entrada:  LELE:   longitud del elemento  %           WELE:   carga distribuida  % Salida:   FEL():  vector de fuerzas distribuida del elemento    FEL = (WELE*LELE/6)*[1 ; 1 ; 4];end% ------------------------------------------------------------------------% Vector de fuerzas equivalentes a una carga distribuida uniforme del elemento % para flexión unidimensional cúbico de continuidad c1function [FEL] = FBEAME(LELE,WELE)  % Entrada:  LELE:   longitud del elemento  %           WELE:   carga distribuida uniforme  % Salida:   FEL():  vector de fuerzas distribuida del elemento    FEL = (WELE*LELE/12)*[6 ; LELE ; 6 ; -LELE];endfunction [FEL] = FBEAMT2(LELE,WELE)  % Entrada:  LELE:   longitud del elemento  %           WELE:   carga distribuida lineal  % Salida:   FEL():  vector de fuerzas distribuida del elemento    FEL = (1/2*WELE*LELE)*[1 ; 0 ; 1 ; 0];endfunction [FEL] = FBEAMT1(LELE,WELE)  % Entrada:  LELE:   longitud del elemento  %           WELE:   carga distribuida lineal  % Salida:   FEL():  vector de fuerzas distribuida del elemento    FEL = (1/2*WELE*LELE)*[1 ; 0 ; 1 ; 0];end